$.cleared=function(A){if($(A).length){$(A).each(function(){var B=$(this).val();$(this).focus(function(){if($(this).val()==B){$(this).val("")}});$(this).blur(function(){if($(this).val()==""){$(this).val(B)}})})}};$(document).ready(function(){$.cleared("input.field")});